596 typedef union SDL_Event
597 {
598     Uint32 type;                            
599     SDL_CommonEvent common;                 
600     SDL_DisplayEvent display;               
601     SDL_WindowEvent window;                 
602     SDL_KeyboardEvent key;                  
603     SDL_TextEditingEvent edit;              
604     SDL_TextInputEvent text;                
605     SDL_MouseMotionEvent motion;            
606     SDL_MouseButtonEvent button;            
607     SDL_MouseWheelEvent wheel;              
608     SDL_JoyAxisEvent jaxis;                 
609     SDL_JoyBallEvent jball;                 
610     SDL_JoyHatEvent jhat;                   
611     SDL_JoyButtonEvent jbutton;             
612     SDL_JoyDeviceEvent jdevice;             
613     SDL_ControllerAxisEvent caxis;          
614     SDL_ControllerButtonEvent cbutton;      
615     SDL_ControllerDeviceEvent cdevice;      
616     SDL_ControllerTouchpadEvent ctouchpad;  
617     SDL_ControllerSensorEvent csensor;      
618     SDL_AudioDeviceEvent adevice;           
619     SDL_SensorEvent sensor;                 
620     SDL_QuitEvent quit;                     
621     SDL_UserEvent user;                     
622     SDL_SysWMEvent syswm;                   
623     SDL_TouchFingerEvent tfinger;           
624     SDL_MultiGestureEvent mgesture;         
625     SDL_DollarGestureEvent dgesture;        
626     SDL_DropEvent drop;                     
628     /* This is necessary for ABI compatibility between Visual C++ and GCC.
629        Visual C++ will respect the push pack pragma and use 52 bytes (size of
630        SDL_TextEditingEvent, the largest structure for 32-bit and 64-bit
631        architectures) for this union, and GCC will use the alignment of the
632        largest datatype within the union, which is 8 bytes on 64-bit
633        architectures.
634 
635        So... we'll add padding to force the size to be 56 bytes for both.
636 
637        On architectures where pointers are 16 bytes, this needs rounding up to
638        the next multiple of 16, 64, and on architectures where pointers are
639        even larger the size of SDL_UserEvent will dominate as being 3 pointers.
640     */
641     Uint8 padding[sizeof(void *) <= 8 ? 56 : sizeof(void *) == 16 ? 64 : 3 * sizeof(void *)];
642 } SDL_Event;
643 
644 /* Make sure we haven't broken binary compatibility */
645 SDL_COMPILE_TIME_ASSERT(SDL_Event, sizeof(SDL_Event) == sizeof(((SDL_Event *)NULL)->padding));
